lay = 'none';        document.querySelector("#moneyDiv").style.display = 'none';        // document.getElementById("open_faq_instructions").style.display = 'none'        // document.getElementById("oppen_video_faq").style.display = 'none'        //отображение верстки до подключения ключей        if (brokers === 1 && user.connected !== true) {//kucoin            console.log("kucoin")            document.getElementById("1kucoin_k").style.display = ''            document.querySelector("#oppen_video_faq").setAttribute("exchange", 1)            document.querySelector("#open_faq_instructions").setAttribute("exchange", 1)        } else if (brokers === 2 && user.connected !== true) {//binance            console.log("binance")            document.getElementById("2binance").style.display = ''            document.querySelector("#oppen_video_faq").setAttribute("exchange", 2)            document.querySelector("#open_faq_instructions").setAttribute("exchange", 2)        } else if (brokers === 3 && user.connected !== true) {//ftx            console.log("ftx")            document.getElementById("3ftx").style.display = ''        }        //отображение верстки после подключение ключей        if (brokers === 1 && user.connected === true) {            document.querySelector("#forcake").setAttribute('style', 'width: 220x !important') //bag fix            document.querySelector("#moneyDiv").style.display = '';            document.querySelector("#roi_table").setAttribute('style', 'display: flex !important')            document.querySelector("#weekly_history_roi").setAttribute('style', 'display: block !important')            document.querySelector("#order_history_table").style.display = ""            document.getElementById("open_faq_instructions").style.display = 'none'            document.getElementById("oppen_video_faq").style.display = 'none'        } else if (brokers === 2 && user.connected === true) {            document.querySelector("#roi_table").setAttribute('style', 'display: none !important')            document.querySelector("#weekly_history_roi").setAttribute('style', 'display: none !important')            document.getElementById("2binance").style.display = ''            document.getElementById("open_faq_instructions").style.display = ''            document.getElementById("oppen_video_faq").style.display = ''            document.querySelector("#oppen_video_faq").setAttribute("exchange", 2)            document.querySelector("#open_faq_instructions").setAttribute("exchange", 2)            document.querySelector("#order_history_table").style.display = "none"        } else if (brokers === 3 && user.connected === true) {            document.querySelector("#roi_table").setAttribute('style', 'display: none !important')            document.querySelector("#weekly_history_roi").setAttribute('style', 'display: none !important')            document.getElementById("3ftx").style.display = ''            document.getElementById("open_faq_instructions").style.display = ''            document.getElementById("oppen_video_faq").style.display = ''            document.querySelector("#order_history_table").style.display = "none"        }    }    function closer_faq_video() {        document.querySelector("#video").style.display = 'none'        document.querySelector("#video_back").style.display = 'none'    }    function open_faq_video(val) {        document.querySelector("#video").style.display = 'block'        document.querySelector("#video_back").style.display = 'block'    }    function open_faq_instructions(val) {        let id = val.attributes.exchange.value;        if (id == "1") { //kucoin video            window.open('https://cunion.io/panel/cabinet/cabinet_faq_api_keys.php')        } else if (id == "2") { //binance vide            window.open('https://cunion.io/panel/cabinet/cabinet_faq_api_keys.php?tag=binance')        }    }</script><script src="js/balance.js"