"        } else if (page_name === "master_account_page") {            document.querySelector("#navigation_master_account").style.color = "#D5E9E8"        } else if (page_name === "cabinet_news") {            document.querySelector("#navigation_news").style.color = "#D5E9E8"        } else if (page_name === "cabinet_gitbook") {            document.querySelector("#navigation_gitbook").style.color = "#D5E9E8"        } else if (page_name === "cabinet_referal") {            document.querySelector("#navigation_referral").style.color = "#D5E9E8"        } else if (page_name === "cabinet_token") {            ExpandTokenLink();            document.querySelector("#navigation_tokenomics").style.color = "#D5E9E8"            document.querySelector("#navigation_token").style.color = "#D5E9E8"        } else if (page_name === "cabinet_airdrop") {            ExpandTokenLink();            document.querySelector("#navigation_airdrop").style.color = "#D5E9E8"            document.querySelector("#navigation_token").style.color = "#D5E9E8"        } else if (page_name === "cabinet_staking") {            ExpandTokenLink();            document.querySelector("#navigation_staking").style.color = "#D5E9E8"            document.querySelector("#navigation_token").style.color = "#D5E9E8"        } else if (page_name == "trader_payments") {            ExpandPaymentsLink();            document.querySelector("#paying_info").style.color = "#D5E9E8"            document.querySelector("#trader_payments").style.color = "#D5E9E8"        } else if (page_name == "referral_payments") {            ExpandPaymentsLink();            document.querySelector("#paying_info").style.color = "#D5E9E8"            document.querySelector("#referral_payments").style.color = "#D5E9E8"        } else if (page_name == "platform_payments") {            ExpandPaymentsLink();            document.querySelector("#paying_info").style.color = "#D5E9E8"            document.querySelector("#platform_payments")